public class Decimal9582 extends SdbTestBase {
    private Sequoiadb sdb;

    private CollectionSpace cs = null;
    private String clName = "cl9582";
    private DBCollection cl = null;
    private String coordAddr;
    private String commCSName;

    @BeforeClass
    public void setUp() {
        this.coordAddr = SdbTestBase.coordUrl;
        this.commCSName = SdbTestBase.csName;
        try {
            sdb = new Sequoiadb( coordAddr, "", "" );
            if ( !sdb.isCollectionSpaceExist( commCSName ) ) {
                sdb.createCollectionSpace( commCSName );
            }
            cs = sdb.getCollectionSpace( commCSName );
            if ( cs.isCollectionExist( clName ) ) {
                cs.dropCollection( clName );
            }
            cl = cs.createCollection( clName );
        } catch ( BaseException e ) {
            Assert.fail( "prepare env failed" + e.getMessage() );
        }
    }

    @AfterClass
    public void tearDown() {
        try {
            if ( cs.isCollectionExist( clName ) ) {
                cs.dropCollection( clName );
            }
            sdb.disconnect();
        } catch ( BaseException e ) {
            Assert.fail( "clear env failed, errMsg:" + e.getMessage() );
        }
    }

    @DataProvider(name = "createDataProvider")
    public Object[][] createData() {
        return new Object[][] { { "9",
                "",
                1000, 0, 1000, 0 },
                { "9", "9.",
                        1000, 999, 1000, 999 },
                { "9", "10.",
                        1000, 999, 1000, 998 },
                { "1", "1.",
                        1000, 999, 1000, 998 },
                { "9", ".",
                        1000, 500, 1000, 500 },
                { "9", ".0",
                        1001, 3, 1000, 1 },
                { "1", "",
                        1003, 3, 1000, 0 },
                { "-9", "-",
                        1000, 0, 1000, 0 },
                { "-9", "-9.",
                        1000, 999, 1000, 999 },
                { "-9", "-10.",
                        1000, 999, 1000, 998 },
                { "-1", "-1.",
                        1000, 999, 1000, 998 },
                { "-9", "-.",
                        1000, 500, 1000, 500 },
                { "-9", "-.0",
                        1001, 3, 1000, 1 },
                { "-1", "-",
                        1003, 3, 1000, 0 }, };
    }

    @Test(dataProvider = "createDataProvider")
    public void test( String basicString, String expectValue, int genPrecision,
            int genScale, int precision, int scale ) {
        // insert data and check
        genDecimalAndCheck( basicString, expectValue, genPrecision, genScale,
                precision, scale );
    }

    @DataProvider(name = "illegalDataProvider")
    public Object[][] createillegalData() {
        return new Object[][] { { "9", 1001, 0, 1000, 0 },
                { "9", 1001, 999, 1000, 999 }, { "9", 1002, 2, 1000, 0 },
                { "1", 1002, 2, 1000, 1 }, { "-9", 1001, 0, 1000, 0 },
                { "-9", 1001, 999, 1000, 999 }, { "-9", 1002, 2, 1000, 0 },
                { "-1", 1002, 2, 1000, 1 }, };
    }

    @Test(dataProvider = "illegalDataProvider")
    public void testAbnormal( String basicString, int genPrecision,
            int genScale, int precision, int scale ) {
        // illegaData check
        try {
            genIllegalDecimalAndCheck( basicString, genPrecision, genScale,
                    precision, scale );
            Assert.fail( "expect result need throw an error but not." );
        } catch ( IllegalArgumentException e ) {
        }
    }

    public void genDecimalAndCheck( String basicString, String expectValue,
            int genPrecision, int genScale, int precision, int scale ) {
        BSONObject obj = new BasicBSONObject();
        StringBuffer basicValue = new StringBuffer( basicString );
        if ( basicString.contains( "-" ) ) {
            basicString = basicString.substring( 1 );
        }
        for ( int i = 0; i < genPrecision - genScale - 1; i++ ) {
            basicValue.append( basicString );
        }
        if ( genScale != 0 ) {
            basicValue.append( "." );
        }

        for ( int j = 0; j < genScale; j++ ) {
            basicValue.append( basicString );
        }

        BigDecimal expectBigDecimal = new BigDecimal( expectValue );

        try {
            BSONDecimal data = new BSONDecimal( basicValue.toString(),
                    precision, scale );
            obj.put( "a", data );
            cl.insert( obj );
            BSONDecimal actualData = ( BSONDecimal ) cl.queryOne().get( "a" );
            BigDecimal actualBigDecimal = actualData.toBigDecimal();
            String actualValue = data.getValue();
            int actualPrecision = data.getPrecision();
            int actualScale = data.getScale();
            Assert.assertEquals( actualData, data );
            Assert.assertEquals( actualValue, expectValue );
            Assert.assertEquals( actualPrecision, precision );
            Assert.assertEquals( actualScale, scale );
            if ( actualBigDecimal.compareTo( expectBigDecimal ) != 0 ) {
                Assert.fail( "compare bigDecimal data failed,expect data:"
                        + expectBigDecimal + ",actual data: "
                        + actualBigDecimal );
            }
            cl.truncate();
        } catch ( IllegalArgumentException e ) {
            Assert.fail( "generate data:" + obj + " failed, errMsg:"
                    + e.getMessage() );
        } catch ( BaseException e ) {
            Assert.fail( "insert data:" + obj + " failed, errMsg:"
                    + e.getMessage() );
        }
    }

    public void genIllegalDecimalAndCheck( String basicString, int genPrecision,
            int genScale, int precision, int scale ) {
        String basicValue = basicString;
        if ( basicString.contains( "-" ) ) {
            basicString = basicString.substring( 1 );
        }
        for ( int i = 0; i < genPrecision - genScale - 1; i++ ) {
            basicValue += basicString;
        }
        if ( genScale != 0 ) {
            basicValue += ".";
        }

        for ( int j = 0; j < genScale; j++ ) {
            basicValue += basicString;
        }
        BSONDecimal data = new BSONDecimal( basicValue, precision, scale );
    }
}
