main( test )
function test ()
{
   var clName = COMMCLNAME + "_7769";
   //clean environment before test
   commDropCL( db, COMMCSNAME, clName );

   //create cl
   var dbcl = commCreateCL( db, COMMCSNAME, clName );

   //insert data
   var doc = [{ a: { $decimal: "1.7E+400" } },
   { a: { $decimal: "-5.94E-400", $precision: [1000, 600] } },
   { a: { $decimal: "0" } }];
   dbcl.insert( doc );

   //check result
   var expRecs = [{ a: { $decimal: "" } },
   { a: { $decimal: "0.", $precision: [1000, 600] } },
   { a: { $decimal: "0" } }];
   checkResult( dbcl, {}, { a: { $abs: 1 } }, expRecs, { _id: 1 } );
   commDropCL( db, COMMCSNAME, clName );
}

