main( test )
function test ()
{
   var clName = COMMCLNAME + "_7790";
   //clean environment before test
   commDropCL( db, COMMCSNAME, clName );

   //create cl
   var dbcl = commCreateCL( db, COMMCSNAME, clName );

   //insert data
   var doc = [{ a: { $decimal: "9223372036854775807198410" } },
   { a: { $decimal: "-9223372036854775808197101" } },
   { a: { $decimal: "-1.7E+398" } },
   { a: { $decimal: "1.7E+378" } },
   { a: { $decimal: "-4.94065645841246544E-380" } },
   { a: { $decimal: "4.94065645841246544E-390" } },
   { a: { $decimal: "0" } },
   { a: { $decimal: ".0258" } }];
   dbcl.insert( doc );

   //check result
   var expRecs = [{ a: { $decimal: "9223372036854775807198410" } },
   { a: { $decimal: "-9223372036854775808197101" } },
   { a: { $decimal: "-" } },
   { a: { $decimal: "" } },
   { a: { $decimal: "-0." } },
   { a: { $decimal: "0." } },
   { a: { $decimal: "0" } },
   { a: { $decimal: "0.0258" } }];

   checkResult( dbcl, {}, {}, expRecs, { _id: 1 } );

   //invalid argument check
   var invalidDoc = { a: { $decimal: "123i567" } };
   invalidDataInsertCheckResult( dbcl, invalidDoc, -6 );
   commDropCL( db, COMMCSNAME, clName );
}

