/************************************************************************
*@Description:  seqDB-8530:导入csv文件时指定decimal类型且指定精度数据校验
*@Author:           2016-8-3  huangxiaoni
************************************************************************/

main( test );

function test ()
{

   var csName = COMMCSNAME;
   var clName = COMMCLNAME + "_8530";
   var cl = readyCL( csName, clName );

   var imprtFile = tmpFileDir + "8530.csv";
   readyData( imprtFile );
   importData( csName, clName, imprtFile );

   checkCLData( cl );
   cleanCL( csName, clName );

}

function readyData ( imprtFile )
{

   var file = fileInit( imprtFile );
   file.write( "1,0\n"
      + "2,9223372036854775807.000000000000000001\n"
      + "3,\n"
      + "4,0.\n"
      + "5,.\n" );
   var fileInfo = cmd.run( "cat " + imprtFile );
   file.close();
}

function importData ( csName, clName, imprtFile )
{

   var decimalFmt = ["a int,b decimal(1,0)",
      "a int,b decimal(37,18)",
      "a int,b decimal(1000,0)",
      "a int,b decimal(1000,999)"];
   for( i = 0; i < decimalFmt.length; i++ )
   {
      var imprtOption = installDir + 'bin/sdbimprt -s ' + COORDHOSTNAME + ' -p ' + COORDSVCNAME
         + ' -c ' + csName + ' -l ' + clName
         + ' --type csv --fields "' + decimalFmt[i]
         + '" --file ' + imprtFile;
      var rc = cmd.run( imprtOption );
      var rcObj = rc.split( "\n" );

      //check import results
      if( i === 0 )
      {
         var expParseRecords = "Parsed records: 2";
         var expParseFailure = "Parsed failure: 3";
         var expImportedRecords = "Imported records: 2";
      }
      else if( i === 1 )
      {
         var expParseRecords = "Parsed records: 3";
         var expParseFailure = "Parsed failure: 2";
         var expImportedRecords = "Imported records: 3";
      }
      else if( i === 2 )
      {
         var expParseRecords = "Parsed records: 4";
         var expParseFailure = "Parsed failure: 1";
         var expImportedRecords = "Imported records: 4";
      }
      else if( i === 3 )
      {
         var expParseRecords = "Parsed records: 2";
         var expParseFailure = "Parsed failure: 3";
         var expImportedRecords = "Imported records: 2";
      }
      var actParseRecords = rcObj[0];
      var actParseFailure = rcObj[1];
      var actImportedRecords = rcObj[4];
      if( expParseRecords !== actParseRecords || expParseFailure !== actParseFailure
         || expImportedRecords !== actImportedRecords )
      {
         throw new Error( "importData fail,[sdbimprt results]" +
            "[" + expParseRecords + ", " + expParseFailure + ", " + expImportedRecords + "]" +
            "[" + actParseRecords + ", " + actParseFailure + ", " + actImportedRecords + "]" );
      }
   }

   // clean tmpRec
   var tmpRec = csName + "_" + clName + "*.rec";
   cmd.run( "rm -rf " + tmpRec );
}

function checkCLData ( cl )
{

   var rc = cl.find( { b: { $type: 1, $et: 100 } }, { _id: { $include: 0 } } ).sort( { a: 1 } );
   var recsArray = [];
   while( tmpRecs = rc.next() )
   {
      recsArray.push( tmpRecs.toObj() );
   }

   var expCnt = 11;
   var expRecs = '[{"a":1,"b":{"$decimal":"0","$precision":[1,0]}},{"a":1,"b":{"$decimal":"0.000000000000000000","$precision":[37,18]}},{"a":1,"b":{"$decimal":"0","$precision":[1000,0]}},{"a":1,"b":{"$decimal":"0.","$precision":[1000,999]}},{"a":2,"b":{"$decimal":"9223372036854775807.000000000000000001","$precision":[37,18]}},{"a":2,"b":{"$decimal":"9223372036854775807","$precision":[1000,0]}},{"a":3,"b":{"$decimal":"","$precision":[1000,0]}},{"a":4,"b":{"$decimal":"1","$precision":[1,0]}},{"a":4,"b":{"$decimal":"0.922337203685477581","$precision":[37,18]}},{"a":4,"b":{"$decimal":"1","$precision":[1000,0]}},{"a":4,"b":{"$decimal":"0.","$precision":[1000,999]}}]';
   var actCnt = recsArray.length;
   var actRecs = JSON.stringify( recsArray );
   if( actCnt !== expCnt || actRecs !== expRecs )
   {
      throw new Error( "checkCLdata fail,[find]" +
         "[cnt:" + expCnt + ", recs:" + expRecs + "]" +
         "[cnt:" + actCnt + ", recs:" + actRecs + "]" );
   }

}